class R2D2Step:
    step = "Check last release SHAs"

    def __init__(self, repo, repo1, console, config):
        self.repo = repo
        self.repo1 = repo1
        self.console = console
        self.config = config

    def run(self):
        check = self.repo1.check_last_release()
        if isinstance(check, Exception):
            self.console.error(str(check))
        else:
            self.console.success("Last release SHAs match")
        self.console.confirm(default=not bool(check))
